const Column = React.createClass({
    render() {
      return (
        <div className="column">
        
        </div>
      );
    }
  })
  
  const Grid = React.createClass({
    render() {
      return (
        <div className="ui grid">
          {this.props.children}
        </div>
      );
    }
  });
  
  const Image = React.createClass({
    render() {
      return (
        <div className="ui image">
          <img src={this.props.src}/>
        </div>
      );
    }
  });
  
  const Keyword = React.createClass({
    getInitialState() {
      return {
        collapsed: true
      };
    },
    
    toggleCollapsed() {
      this.setState({
        collapsed: !this.state.collapsed
      });
    },
    
    render() {
      const { name, data } = this.props;
      const { collapsed } = this.state;
      const scoreDir = collapsed ? 'right pointing' : 'pointing below';
      
      const innerContent = collapsed ? '' : (
        <div className="description">
          Test
        </div>
      );
      
      return (
          <div className="item">
            <div className="content">
              <a className="header" onClick={this.toggleCollapsed}>
                <span>
                  <div className={`ui inverted black ${scoreDir} label`}>
                    {data.score}
                  </div>
                  {name}
                </span>
              </a>
              {innerContent}
            </div>
          </div>
      );
    }
  });
  
  const CardAccordion = React.createClass({
    getInitialState() {
      return {
        collapsed: true
      };
    },
    
    toggleCollapsed() {
      this.setState({
        collapsed: !this.state.collapsed
      });
    },
    
    render() {
      const { header, children } = this.props;
      const { collapsed } = this.state;    
  
      const dropdownIcon = collapsed ? <i className="caret right icon"></i> : <i className="caret down icon"></i>;
      
      const results = collapsed ? '' : children;
      
      return (
        <div className="content">
          <a className="ui sub header" onClick={this.toggleCollapsed}>
            <span>
              {dropdownIcon}
              {header}
            </span>
          </a>
          {results}
        </div>
      );
    }
  });
  
  
  const Card = React.createClass({
    render() {
      return (
        <div className="ui fluid card">
          <div className="content">
            <div className="ui header">
              {this.props.header}
            </div>
          </div>
          {this.props.children}
        </div>
      );
    }
  });
  
  const Container = React.createClass({
    render() {
      let { text } = this.props;
      text = text ? 'text' : '';
      return (
        <div className={`ui ${text} container`}>
          {this.props.children}
        </div>
      );
    }
  });
  
  const App = React.createClass({
    render() {
      return (
        <div className='app-container'>
          <Container text>
            <Card header="Test Testerson">
              <CardAccordion header="Bob Ross">
                It's life. It's interesting. It's fun. Happy painting, God bless. How to paint. That's easy. What to paint. That's much harder. Remember how free clouds are. They just lay around in the sky all day long. You can create beautiful things - but you have to see them in your mind first. Let's do it again then, what the heck.
              </CardAccordion>
              <CardAccordion header="Image">
              <Image src="https://semantic-ui.com/images/wireframe/image.png" />
              </CardAccordion>
              <CardAccordion header="Stats">
                <div className="ui items">
                <Keyword name="person" data={{score:5}} />
                <Keyword name="place" data={{score:2}} />
                <Keyword name="thing" data={{score:-10}} />
                </div>
              </CardAccordion>
            </Card>
          </Container>
        </div>
      );
    }
  });
  
